import { Container, List } from "@mui/material";
import React, { useState } from "react";
import Form from "../components/Form";
import TodoItem from "../components/TodoItem";
import "./style.css";

export default function Home() {
  const [todos, setTodos] = useState([]);
  const addTodo = (todo) => {
    setTodos([...todos, todo]);
  };

  const deleteTodo = (id) => {
    var filtered = todos.filter((todo) => todo.id !== id);
    setTodos(filtered);
  };

  const editTodo = (id, editedText) => {
    var todosArray = todos;

    for (var i in todosArray) {
      if (todosArray[i].id == id) {
        todosArray[i].text = editedText;
      }
    }

    // console.log(todosArray);
    // todosArray.splice(todosArray.id, 1, { text: editedText, id: id });
    // console.log(todosArray);
    setTodos(todosArray);
  };

  return (
    <><Container maxWidth="xs" style={{ marginTop: "1em" }}>
      <Form addTodo={addTodo} />
      <List sx={{ marginTop: "1em" }}>
        {todos.map((todo) => (
          <div key={todo.id} style={{ marginTop: "1em" }}>
            <TodoItem editTodo={editTodo} todo={todo} deleteTodo={deleteTodo} />
          </div>
        ))}
      </List>
    </Container><div class="todo-app">


        <div className="todo-app">
            <h1>todos</h1>
            <input
                className="todo-input"
                type="text"
                placeholder="What needs to be done?"
                value={newTodo}
                onChange={e => setNewTodo(e.target.value)}
                onKeyPress={e => e.key === 'Enter' && addTodo()}
            />
            <ul className="todo-list">
                {filteredTodos.map(todo => (
                    <li key={todo.id} className="todo-item">
                        <input
                            type="checkbox"
                            checked={todo.completed}
                            onChange={() => toggleTodo(todo.id)}
                        />
                        <span>{todo.text}</span>
                    </li>
                ))}
            </ul>
            <div className="footer">
                <span>{todos.filter(todo => !todo.completed).length} item left!</span>
                <div>
                    <button className={filter === 'all' ? 'selected' : ''} onClick={() => setFilter('all')}>All</button>
                    <button className={filter === 'active' ? 'selected' : ''} onClick={() => setFilter('active')}>Active</button>
                    <button className={filter === 'completed' ? 'selected' : ''} onClick={() => setFilter('completed')}>Completed</button>
                </div>
                <button onClick={clearCompleted}>Clear completed</button>
            </div>
        </div>
    );



