          <li class="todo-item">
            <input type="checkbox" />
            <span>aaaaaa</span>
          </li>
        </ul>
        <div class="footer">
          <span>1 item left!</span>
          <div>
            <button class="selected">All</button>
            <button>Active</button>
            <button>Completed</button>
          </div>
          <button>Clear completed</button>
        </div>
      </div></>
  );
}
