import React, { useState, useEffect } from "react";
import "./style.css";

export default function Home() {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState("all");
  const [input, setInput] = useState("");

  useEffect(() => {
    teste();
  }, []);

  function teste() {
    alert("teste");
  }

  function addTodo() {
    if (input.trim() !== "") {
      const newTodo = {
        id: Date.now(),
        text: input,
        completed: false,
      };
      setTodos([...todos, newTodo]);
      setInput("");
    }
  }

  function toggleTodoCompletion(id) {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  }

  function removeTodo(id) {
    setTodos(todos.filter((todo) => todo.id !== id));
  }

  function clearCompleted() {
    setTodos(todos.filter((todo) => !todo.completed));
  }

  function filteredTodos() {
    if (filter === "active") {
      return todos.filter((todo) => !todo.completed);
    } else if (filter === "completed") {
      return todos.filter((todo) => todo.completed);
    }
    return todos;
  }

  return (
    <>
      <h1>todos</h1>
      <div className="todo-app">
        <input
          type="text"
          className="todo-input"
          placeholder="O que voce quer fazer?"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && addTodo()}
        />
        <ul className="todo-list">
          {filteredTodos().map((todo) => (
            <li key={todo.id} className={`todo-item ${todo.completed ? "completed" : ""}`}>
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => toggleTodoCompletion(todo.id)}
              />
              <span>{todo.text}</span>
              <button onClick={() => removeTodo(todo.id)}>Delete</button>
            </li>
          ))}
        </ul>
        <div className="footer">
          <span className="item-count">{todos.length} item(s) left!</span>
          <div className="filters">
            <button className={filter === "all" ? "selected" : ""} onClick={() => setFilter("all")}>All</button>
            <button className={filter === "active" ? "selected" : ""} onClick={() => setFilter("active")}>Active</button>
            <button className={filter === "completed" ? "selected" : ""} onClick={() => setFilter("completed")}>Completed</button>
          </div>
          <button onClick={clearCompleted}>Clear completed</button>
        </div>
      </div>
    </>
  );
}
